INSERT INTO CLINICAL_TRIAL_TYPE(id, name, clinical_trial_type_description) VALUES ('9de5c971-eec1-4624-bbdd-24705b42cf96', 'Quality of life', 'Also known as “supportive care,” this research explores ways to improve comfort and the quality of life for individuals with a chronic illness.');
INSERT INTO CLINICAL_TRIAL_TYPE(id, name, clinical_trial_type_description) VALUES ('09aef28a-cfba-4073-aa23-719bd94a0fc5', 'Treatment', 'Treatment research (also called “clinical trials”) generally involves an intervention such as medication, psychotherapy, new devices, or new approaches to surgery or radiation therapy.');
INSERT INTO CLINICAL_TRIAL_TYPE(id, name, clinical_trial_type_description) VALUES ('912ced79-8d17-405a-a770-f3b9a1b076ae', 'Prevention', 'Prevention research looks for better ways to prevent disorders from developing or returning. Different kinds of prevention research may study medicines, vitamins, vaccines, minerals, or lifestyle changes.');
INSERT INTO CLINICAL_TRIAL_TYPE(id, name, clinical_trial_type_description) VALUES ('010cce3a-aebd-4244-9e4c-7ca96f2f225f', 'Diagnostic', 'This refers to the practice of looking for better ways to identify a particular disorder or condition.');
INSERT INTO CLINICAL_TRIAL_TYPE(id, name, clinical_trial_type_description) VALUES ('44ab8d69-e073-445e-969a-f66303a5761d', 'Screening', 'Screening research aims to find the best ways to detect certain disorders or health conditions.');
INSERT INTO CLINICAL_TRIAL_TYPE(id, name, clinical_trial_type_description) VALUES ('c9d8fdcc-f0c9-4fc3-ae4e-634bd445b878', 'Genetic studies', 'Genetic studies aim to improve the prediction of disorders by identifying and understanding how genes and illnesses may be related. Research in this area may explore ways in which a person’s genes make him or her more or less likely to develop a disorder. This may lead to development of tailor-made treatments based on a patient’s genetic make-up.');
INSERT INTO CLINICAL_TRIAL_TYPE(id, name, clinical_trial_type_description) VALUES ('a5541d22-0f88-4b5b-ab1d-9a29756b774b', 'Epidemiological studies', 'Epidemiological studies seek to identify the patterns, causes, and control of disorders in groups of people.');
INSERT INTO CLINICAL_TRIAL_PHASE(id, name, clinical_trial_phase_description) VALUES ('3ef9ff99-126e-4eed-88a3-f4cdc15da122', 'Phase I trials', 'Researchers test an experimental drug or treatment in a small group of people for the first time. The researchers evaluate the treatment’s safety, determine a safe dosage range, and identify side effects.');
INSERT INTO CLINICAL_TRIAL_PHASE(id, name, clinical_trial_phase_description) VALUES ('73561c78-0e22-48f5-a3dd-c05a6db223bd', 'Phase II trials', 'The experimental drug or treatment is given to a larger group of people to see if it is effective and to further evaluate its safety.');
INSERT INTO CLINICAL_TRIAL_PHASE(id, name, clinical_trial_phase_description) VALUES ('ae96200d-aee6-4965-a14f-6d4b63b3b1b3', 'Phase III trials', 'The experimental study drug or treatment is given to large groups of people. Researchers confirm its effectiveness, monitor side effects, compare it to commonly used treatments, and collect information that will allow the experimental drug or treatment to be used safely.');
INSERT INTO CLINICAL_TRIAL_PHASE(id, name, clinical_trial_phase_description) VALUES ('d3ae3403-6296-4f4a-a8b5-178e230f36db', 'Phase IV trials', 'Post-marketing studies, which are conducted after a treatment is approved for use by the FDA, provide additional information including the treatment or drug’s risks, benefits, and best use.');
